body:
curl -X POST \
  https://www.wixapis.com/stores/v1/subscription-options/createBulk \
  --data-binary '{
    "subscriptionOptions": [
      {
        "title": "Monthly auto-renewal",
        "subscriptionSettings": {
          "frequency": "MONTH",
          "autoRenewal": true
        }
      },
      {
        "title": "biweekly (2 month)",
        "description": "save 10%",
        "subscriptionSettings": {
          "frequency": "WEEK",
          "interval": 2,
          "billingCycles": 4
        },
        "discount": {
          "type": "PERCENT",
          "value": 10
        }
      }
    ]
    }'\
  -H 'Content-Type: application/json' \
  -H 'Authorization: <AUTH>'

curl -X PATCH \
  https://www.wixapis.com/stores/v1/subscription-options \
  --data-binary '{
    "subscriptionOptions": [
      {
        "id": "26334df1-9ea6-43d5-9a51-acbc9d82690f",
        "title": "Monthly auto-renewal",
        "subscriptionSettings": {
          "frequency": "MONTH",
          "autoRenewal": true
        }
      },
      {
        "id": "87d201b5-f8f6-4e60-905f-88815c2c84ec",
        "title": "biweekly (2 month)",
        "description": "save 10%",
        "subscriptionSettings": {
          "frequency": "WEEK",
          "interval": 2,
          "billingCycles": 4
        },
        "discount": {
          "type": "PERCENT",
          "value": 10
        }
      }
    ]
  }'\
  -H 'Content-Type: application/json' \
  -H 'Authorization: <AUTH>'
